INSERT INTO `wp_posts` VALUES(2, 1, '2012-08-01 17:37:13', '2012-08-02 00:37:13', 'The UCLA Events Office is your single source for event planning and production on the UCLA campus. We schedule commercial film and photography shoots, weddings, corporate events and private parties. In addition, we offer general event planning services and equipment rentals. Whether you are planning a small outdoor reception or a multi-venue symposium, our 50 year history with UCLA will enable you to smoothly navigate the ins and outs of a large public institution.', 'About Us', '', 'publish', 'closed', 'closed', '', 'about-us-2', '', '', '2012-09-27 14:53:46', '2012-09-27 21:53:46', '', 0, 'http://uclaevents.com/development/ucla-events/?page_id=2', 0, 'page', '', 0);
