INSERT INTO `wp_posts` VALUES(93, 1, '2012-08-15 10:06:32', '2012-08-15 17:06:32', 'The UCLA Events Office is the leading supplier of event equipment rentals including tables, chairs, tents, linens and other items. With both our own in-stock inventory and special access to vendor discounts, the Events Office is your one-stop shop for all your equipment needs.\r\n\r\nSubmit your online Event Equipment Rental Request right now or take advantage of our professional event management services. Whether you are organizing a small reception or a campus-wide affair, the Events Office has the knowledge and the resources to make your event a success.', 'Equipment Rentals', '', 'inherit', 'open', 'open', '', '39-revision-6', '', '', '2012-08-15 10:06:32', '2012-08-15 17:06:32', '', 39, 'http://uclaevents.com/development/ucla-events/39-revision-6/', 0, 'revision', '', 0);
